mapper.select:
		SELECT 
        FROM t_project_change_log project_change_log_
        WHERE project_change_log_.id = #{id,jdbcType=BIGINT}

mapper.update:
UPDATE t_project_change_log
project_id = #{projectId,jdbcType=BIGINT}, `name` = #{name,jdbcType=VARCHAR}, note = #{note,jdbcType=VARCHAR}, version = #{version,jdbcType=VARCHAR}, url = #{url,jdbcType=VARCHAR}, issue_change_date = #{issueChangeDate,jdbcType=TIMESTAMP}, create_date = #{createDate,jdbcType=TIMESTAMP},
WHERE id = #{id,jdbcType=BIGINT}
