mapper.select:
		SELECT 
        FROM t_component component_
        WHERE component_.id = #{id,jdbcType=BIGINT}

mapper.update:
UPDATE t_component
code = #{code,jdbcType=VARCHAR}, project_id = #{projectId,jdbcType=BIGINT}, name = #{name,jdbcType=VARCHAR}, state = #{state,jdbcType=INTEGER}, area_level = #{areaLevel,jdbcType=INTEGER}, parent_id = #{parentId,jdbcType=INTEGER}, parent_name = #{parentName,jdbcType=VARCHAR}, note = #{note,jdbcType=VARCHAR}, url = #{url,jdbcType=VARCHAR}, create_date = #{createDate,jdbcType=TIMESTAMP}, update_date = #{updateDate,jdbcType=TIMESTAMP},
WHERE id = #{id,jdbcType=BIGINT}
