mapper.select:
		SELECT 
        FROM t_project project_
        WHERE project_.id = #{id,jdbcType=BIGINT}

mapper.update:
UPDATE t_project
code = #{code,jdbcType=VARCHAR}, `name` = #{name,jdbcType=VARCHAR}, description = #{description,jdbcType=VARCHAR}, author_id = #{authorId,jdbcType=BIGINT}, author_name = #{authorName,jdbcType=VARCHAR}, url = #{url,jdbcType=VARCHAR}, project_type = #{projectType,jdbcType=VARCHAR}, assignee_type = #{assigneeType,jdbcType=VARCHAR}, avatar_id = #{avatarId,jdbcType=BIGINT}, state = #{state,jdbcType=INTEGER}, keyword = #{keyword,jdbcType=VARCHAR}, version = #{version,jdbcType=VARCHAR}, create_date = #{createDate,jdbcType=TIMESTAMP}, update_date = #{updateDate,jdbcType=TIMESTAMP},
WHERE id = #{id,jdbcType=BIGINT}
